mapper.select:
select 
from message
where status != 2 and from_id != 1
and conversation_id=#{conversationId}
order by id desc
limit #{offset},#{limit}

select count(id) from message
where status != 2 and from_id != 1
and conversation_id=#{conversationId}

select count(id) from message
where status = 0 and from_id != 1
and to_id = #{userId}

    and conversation_id = #{conversationId}

select count(id) from message
where from_id=1 and to_id=#{userId}
and status = 0

    and conversation_id=#{topic}

select  from message
where status != 2
and from_id = 1 and to_id = ${userId} and conversation_id = #{topic}
order by create_time desc
limit #{offset}, #{limit}

mapper.update:
